    const accessibilityEnhancer = new AccessibilityEnhancer(dashboard);
    const errorHandler = new ErrorHandler(dashboard);
    const deploymentPreparer = new DeploymentPreparer(dashboard);
    
    // Link components together
    dashboard.performanceOptimizer = performanceOptimizer;
    dashboard.accessibilityEnhancer = accessibilityEnhancer;
    dashboard.errorHandler = errorHandler;
    
    // Make dashboard globally available for debugging
    window.financeDashboard = dashboard;
    
    // Show welcome message
    dashboard.showNotification(
        'Finance Dashboard loaded successfully!',
        'success'
    );
    
    // Log initialization
