            app.get('/api/books', (req, res) => {
                // Optional query parameters
                const { genre, search, sort, page = 1, limit = 10 } = req.query;
                
                let filteredBooks = [...books];
                
                // Filter by genre
                if (genre) {
                    filteredBooks = filteredBooks.filter(book => 
                        book.genre.toLowerCase() === genre.toLowerCase()
                    );
                }
                
                // Search by title or author
                if (search) {
                    filteredBooks = filteredBooks.filter(book =>
                        book.title.toLowerCase().includes(search.toLowerCase()) ||
                        book.author.toLowerCase().includes(search.toLowerCase())
                    );
                }
                
                // Sort books
                if (sort) {
                    filteredBooks.sort((a, b) => {
                        if (sort === 'price') return a.price - b.price;
                        if (sort === 'title') return a.title.localeCompare(b.title);
                        if (sort === 'year') return a.publishedYear - b.publishedYear;
                        return 0;
                    });
                }
                
                // Pagination
                const startIndex = (page - 1) * limit;
                const endIndex = page * limit;
                const paginatedBooks = filteredBooks.slice(startIndex, endIndex);
                
                res.json({
                    total: filteredBooks.length,
                    page: parseInt(page),
                    limit: parseInt(limit),
                    totalPages: Math.ceil(filteredBooks.length / limit),
                    books: paginatedBooks
                });
            });
            
            // GET /api/books/:id - Get single book
            app.get('/api/books/:id', (req, res) => {
                const bookId = parseInt(req.params.id);
                const book = books.find(b => b.id === bookId);
                
                if (!book) {
                    return res.status(404).json({ error: 'Book not found' });
                }
                
                res.json(book);
            });
            
            // POST /api/books - Create new book
            app.post('/api/books', (req, res) => {
                const { title, author, genre, price, stock, publishedYear } = req.body;
                
                // Validation
                if (!title || !author || !genre || !price) {
                    return res.status(400).json({ 
                        error: 'Missing required fields: title, author, genre, price' 
                    });
                }
                
                const newBook = {
                    id: nextId++,
                    title,
                    author,
                    genre,
                    price: parseFloat(price),
                    stock: stock ? parseInt(stock) : 0,
                    publishedYear: publishedYear ? parseInt(publishedYear) : null
                };
                
                books.push(newBook);
                
                res.status(201).json({
                    message: 'Book created successfully',
                    book: newBook
                });
            });
            
            // PUT /api/books/:id - Update entire book
            app.put('/api/books/:id', (req, res) => {
                const bookId = parseInt(req.params.id);
                const index = books.findIndex(b => b.id === bookId);
                
                if (index === -1) {
                    return res.status(404).json({ error: 'Book not found' });
                }
                
                const { title, author, genre, price, stock, publishedYear } = req.body;
                
                // Validation
                if (!title || !author || !genre || !price) {
                    return res.status(400).json({ 
                        error: 'Missing required fields: title, author, genre, price' 
                    });
                }
                
                books[index] = {
                    id: bookId,
                    title,
                    author,
                    genre,
                    price: parseFloat(price),
                    stock: stock ? parseInt(stock) : 0,
                    publishedYear: publishedYear ? parseInt(publishedYear) : null
                };
                
                res.json({
                    message: 'Book updated successfully',
                    book: books[index]
                });
            });
            
            // PATCH /api/books/:id - Update partial book
            app.patch('/api/books/:id', (req, res) => {
                const bookId = parseInt(req.params.id);
                const index = books.findIndex(b => b.id === bookId);
                
                if (index === -1) {
                    return res.status(404).json({ error: 'Book not found' });
                }
                
                // Update only provided fields
                const updates = req.body;
                books[index] = { ...books[index], ...updates };
                
                res.json({
                    message: 'Book partially updated',
                    book: books[index]
                });
            });
            
            // DELETE /api/books/:id - Delete book
            app.delete('/api/books/:id', (req, res) => {
                const bookId = parseInt(req.params.id);
                const initialLength = books.length;
                
                books = books.filter(b => b.id !== bookId);
                
                if (books.length === initialLength) {
                    return res.status(404).json({ error: 'Book not found' });
                }
                
                res.json({ message: 'Book deleted successfully' });
            });
            
            // Error handling middleware
            app.use((err, req, res, next) => {
                console.error(err.stack);
                res.status(500).json({ error: 'Something went wrong!' });
            });
            
            // Start server
            const PORT = process.env.PORT || 3000;
            app.listen(PORT, () => {
                console.log(\`Bookstore API running on port \${PORT}\`);
            });
        </pre>
    </div>
    
    <b>Testing Your API:</b>
