    <h3>Common MongoDB Mistakes</h3>
    
    <div style="background-color: #ffe6e6; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            // 1. Not using indexes properly
            // ‚ùå Bad: Querying without indexes on filtered fields
            db.users.find({ email: "user@example.com" }); // Without index on email
            
            // ‚úÖ Good: Create appropriate indexes
            db.users.createIndex({ email: 1 }); // Create index
            db.users.find({ email: "user@example.com" }).explain(); // Check query plan
            
            // 2. Fetching too much data
            // ‚ùå Bad: Fetching entire documents when only need few fields
            const users = await User.find(); // Gets all fields
            
            // ‚úÖ Good: Use projection
            const users = await User.find({}, 'name email'); // Only name and email
            
            // 3. Not handling connection errors
            // ‚ùå Bad: No error handling for database connection
            mongoose.connect(uri);
            
            // ‚úÖ Good: Proper error handling
            mongoose.connect(uri, {
                useNewUrlParser: true,
                useUnifiedTopology: true
            }).then(() => console.log('Connected'))
              .catch(err => console.error('Connection failed:', err));
            
            // 4. Not using transactions when needed
            // ‚ùå Bad: Multiple operations without transaction
            await order.save();
            await inventory.update({ productId }, { $inc: { stock: -quantity } });
            // If second operation fails, order is saved but inventory not updated
            
            // ‚úÖ Good: Use transactions for atomic operations
            const session = await mongoose.startSession();
            session.startTransaction();
            
            try {
                await order.save({ session });
                await inventory.update(
                    { productId },
                    { $inc: { stock: -quantity } },
                    { session }
                );
                
                await session.commitTransaction();
            } catch (error) {
                await session.abortTransaction();
                throw error;
            } finally {
                session.endSession();
            }
            
            // 5. Over-embedding documents
            // ‚ùå Bad: Embedding thousands of comments in a blog post
            {
                _id: 1,
                title: "Blog Post",
                comments: [/* thousands of comments */]
            }
            
            // ‚úÖ Good: Reference comments as separate collection
            {
                _id: 1,
                title: "Blog Post"
            }
            // Comments collection
            { _id: 101, postId: 1, text: "Comment 1" }
            { _id: 102, postId: 1, text: "Comment 2" }
            
            // 6. Not setting query timeouts
            // ‚ùå Bad: Query could run forever
            const result = await collection.find({}).toArray();
            
            // ‚úÖ Good: Set timeout
            const result = await collection.find({})
                .maxTimeMS(5000) // 5 second timeout
                .toArray();
            
            // 7. Not validating data before saving
            // ‚ùå Bad: Saving any data without validation
            const user = new User(req.body);
            await user.save();
            
            // ‚úÖ Good: Define schema with validation
            const userSchema = new Schema({
                email: {
                    type: String,
                    required: true,
                    unique: true,
                    validate: {
                        validator: function(v) {
                            return /^[^\\s@]+@[^\\s@]+\\.[^\\s@]+$/.test(v);
                        },
                        message: 'Invalid email address'
                    }
                }
            });
        </pre>
    </div>
    
