<b>f. Role-Based Access Control (RBAC)</b><br/>

<b>What is RBAC?</b>
<div style="background-color: #f0f8ff; padding: 15px; margin: 10px 0; border-radius: 5px;">
    <b>RBAC (Role-Based Access Control)</b>: Access control based on roles assigned to users<br/>
    
    <b>Components of RBAC:</b>
    <ul>
        <li><b>User</b>: Person or system that needs access</li>
        <li><b>Role</b>: Job function with specific permissions</li>
        <li><b>Permission</b>: Approval to perform an operation</li>
        <li><b>Resource</b>: Object being accessed (e.g., document, API endpoint)</li>
    </ul>
    
    <b>Common Role Hierarchy:</b>
    <pre>
        Super Admin ‚Üí Admin ‚Üí Manager ‚Üí User ‚Üí Guest
    </pre>
</div>

<b>Implementing RBAC:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
    <pre>
        // 1. Define user roles and permissions
        // constants/roles.js
        const ROLES = {
            SUPER_ADMIN: 'super_admin',
            ADMIN: 'admin',
            MODERATOR: 'moderator',
            USER: 'user',
            GUEST: 'guest'
        };
        
        const PERMISSIONS = {
            // User management
            USER_CREATE: 'user:create',
            USER_READ: 'user:read',
            USER_UPDATE: 'user:update',
            USER_DELETE: 'user:delete',
            
            // Content management
            POST_CREATE: 'post:create',
            POST_READ: 'post:read',
            POST_UPDATE: 'post:update',
            POST_DELETE: 'post:delete',
            
            // Admin permissions
            ADMIN_PANEL_ACCESS: 'admin:access',
            SYSTEM_SETTINGS_UPDATE: 'system:update'
        };
        
        // Role-Permission mapping
        const ROLE_PERMISSIONS = {
            [ROLES.SUPER_ADMIN]: [
                PERMISSIONS.USER_CREATE,
                PERMISSIONS.USER_READ,
                PERMISSIONS.USER_UPDATE,
                PERMISSIONS.USER_DELETE,
                PERMISSIONS.POST_CREATE,
                PERMISSIONS.POST_READ,
                PERMISSIONS.POST_UPDATE,
                PERMISSIONS.POST_DELETE,
                PERMISSIONS.ADMIN_PANEL_ACCESS,
                PERMISSIONS.SYSTEM_SETTINGS_UPDATE
            ],
            [ROLES.ADMIN]: [
                PERMISSIONS.USER_CREATE,
                PERMISSIONS.USER_READ,
                PERMISSIONS.USER_UPDATE,
                PERMISSIONS.POST_CREATE,
                PERMISSIONS.POST_READ,
                PERMISSIONS.POST_UPDATE,
                PERMISSIONS.POST_DELETE,
                PERMISSIONS.ADMIN_PANEL_ACCESS
            ],
            [ROLES.MODERATOR]: [
                PERMISSIONS.POST_READ,
                PERMISSIONS.POST_UPDATE,
                PERMISSIONS.POST_DELETE
            ],
            [ROLES.USER]: [
                PERMISSIONS.POST_CREATE,
                PERMISSIONS.POST_READ,
                PERMISSIONS.POST_UPDATE,
                PERMISSIONS.POST_DELETE
            ],
            [ROLES.GUEST]: [
                PERMISSIONS.POST_READ
            ]
        };
        
        module.exports = {
            ROLES,
            PERMISSIONS,
            ROLE_PERMISSIONS
        };
        
        // 2. User schema with roles
        // models/User.js
        const { ROLES } = require('../constants/roles');
        
        const userSchema = new Schema({
            email: String,
            password: String,
            role: {
                type: String,
                enum: Object.values(ROLES),
                default: ROLES.USER
            },
            permissions: [String], // Custom permissions override role
            isActive: { type: Boolean, default: true },
            lastLogin: Date
        });
        
        // Method to check if user has permission
        userSchema.methods.hasPermission = function(permission) {
            // Check custom permissions first
            if (this.permissions && this.permissions.includes(permission)) {
                return true;
            }
            
            // Check role permissions
            const rolePermissions = ROLE_PERMISSIONS[this.role] || [];
            return rolePermissions.includes(permission);
        };
        
        // Method to check if user has any of the permissions
        userSchema.methods.hasAnyPermission = function(permissions) {
            return permissions.some(permission => this.hasPermission(permission));
        };
        
        // Method to check if user has all permissions
        userSchema.methods.hasAllPermissions = function(permissions) {
            return permissions.every(permission => this.hasPermission(permission));
        };
        
        // 3. Permission-based middleware
        // middleware/permissionMiddleware.js
        const { PERMISSIONS } = require('../constants/roles');
        
        // Single permission check
        const hasPermission = (permission) => {
            return (req, res, next) => {
                if (!req.user) {
                    return res.status(401).json({
                        success: false,
                        error: 'Authentication required'
                    });
                }
                
                if (!req.user.hasPermission(permission)) {
                    return res.status(403).json({
                        success: false,
                        error: 'Insufficient permissions'
                    });
                }
                
                next();
            };
        };
        
        // Any of multiple permissions
        const hasAnyPermission = (...permissions) => {
            return (req, res, next) => {
                if (!req.user) {
                    return res.status(401).json({
                        success: false,
                        error: 'Authentication required'
                    });
                }
                
                if (!req.user.hasAnyPermission(permissions)) {
                    return res.status(403).json({
                        success: false,
                        error: 'Insufficient permissions'
                    });
                }
                
                next();
            };
        };
        
        // All of multiple permissions
        const hasAllPermissions = (...permissions) => {
            return (req, res, next) => {
                if (!req.user) {
                    return res.status(401).json({
                        success: false,
                        error: 'Authentication required'
                    });
                }
                
                if (!req.user.hasAllPermissions(permissions)) {
                    return res.status(403).json({
                        success: false,
                        error: 'Insufficient permissions'
                    });
                }
                
                next();
            };
        };
        
        // Ownership check (user can only access their own resources)
        const isOwnerOrHasPermission = (resourcePath, permission) => {
            return async (req, res, next) => {
                if (!req.user) {
                    return res.status(401).json({
                        success: false,
                        error: 'Authentication required'
                    });
                }
                
                try {
                    // Get resource ID from request
                    const resourceId = req.params.id || req.body.id;
                    
                    if (!resourceId) {
                        return next();
                    }
                    
                    // Get resource from database
                    const Resource = require(\`../models/\${resourcePath}\`);
                    const resource = await Resource.findById(resourceId);
                    
                    if (!resource) {
                        return res.status(404).json({
                            success: false,
                            error: 'Resource not found'
                        });
                    }
                    
                    // Check if user owns the resource
                    const isOwner = resource.userId && 
                                    resource.userId.toString() === req.user._id.toString();
                    
                    // Check if user has permission
                    const hasPerm = req.user.hasPermission(permission);
                    
                    if (isOwner || hasPerm) {
                        // Add resource to request for later use
                        req.resource = resource;
                        next();
                    } else {
                        return res.status(403).json({
                            success: false,
                            error: 'Access denied'
                        });
                    }
                } catch (error) {
                    console.error('Ownership check error:', error);
                    res.status(500).json({
                        success: false,
                        error: 'Authorization check failed'
                    });
                }
            };
        };
        
        module.exports = {
            hasPermission,
            hasAnyPermission,
            hasAllPermissions,
            isOwnerOrHasPermission
        };
        
        // 4. Using permission middleware in routes
        // routes/posts.js
        const express = require('express');
        const router = express.Router();
        const { protect } = require('../middleware/authMiddleware');
        const { 
            hasPermission, 
            hasAnyPermission,
            isOwnerOrHasPermission 
        } = require('../middleware/permissionMiddleware');
        const { PERMISSIONS } = require('../constants/roles');
        
        // Public routes
        router.get('/', getPosts); // Anyone can read
        
        // Protected routes
        router.post('/', 
            protect, 
            hasPermission(PERMISSIONS.POST_CREATE), 
            createPost
        );
        
        // User can update their own post OR admin can update any
        router.put('/:id',
            protect,
            isOwnerOrHasPermission('Post', PERMISSIONS.POST_UPDATE),
            updatePost
        );
        
        // User can delete their own post OR admin can delete any
        router.delete('/:id',
            protect,
            isOwnerOrHasPermission('Post', PERMISSIONS.POST_DELETE),
            deletePost
        );
        
        // Admin-only route
        router.get('/analytics',
            protect,
            hasPermission(PERMISSIONS.ADMIN_PANEL_ACCESS),
            getPostAnalytics
        );
        
        // 5. Dynamic permission checking in controllers
        // controllers/postController.js
        const updatePost = async (req, res) => {
            try {
                const postId = req.params.id;
                const updates = req.body;
                
                // Check permissions dynamically
                if (updates.status === 'published') {
                    if (!req.user.hasPermission(PERMISSIONS.POST_PUBLISH)) {
                        return res.status(403).json({
                            success: false,
                            error: 'Permission required to publish posts'
                        });
                    }
                }
                
                // Proceed with update...
            } catch (error) {
                // Handle error
            }
        };
        
        // 6. Admin panel for managing roles and permissions
        // routes/adminRoutes.js
        router.put('/users/:id/role', 
            protect,
            hasPermission(PERMISSIONS.USER_UPDATE),
            async (req, res) => {
                try {
                    const { id } = req.params;
                    const { role } = req.body;
                    
                    // Validate role
                    if (!Object.values(ROLES).includes(role)) {
                        return res.status(400).json({
                            success: false,
                            error: 'Invalid role'
                        });
                    }
                    
                    // Cannot change super_admin role (except by super_admin)
                    if (req.user.role !== ROLES.SUPER_ADMIN && role === ROLES.SUPER_ADMIN) {
                        return res.status(403).json({
                            success: false,
                            error: 'Only super admin can assign super admin role'
                        });
                    }
                    
                    const user = await User.findById(id);
                    
                    if (!user) {
                        return res.status(404).json({
                            success: false,
                            error: 'User not found'
                        });
                    }
                    
                    // Update role
                    user.role = role;
                    await user.save();
                    
                    res.json({
                        success: true,
                        message: \`User role updated to \${role}\`
                    });
                } catch (error) {
                    console.error('Role update error:', error);
                    res.status(500).json({
                        success: false,
                        error: 'Failed to update role'
                    });
                }
            }
        );
        
        router.put('/users/:id/permissions',
            protect,
            hasPermission(PERMISSIONS.USER_UPDATE),
            async (req, res) => {
                try {
                    const { id } = req.params;
                    const { permissions } = req.body;
                    
                    const user = await User.findById(id);
                    
                    if (!user) {
                        return res.status(404).json({
                            success: false,
                            error: 'User not found'
                        });
                    }
                    
                    // Validate permissions
                    const validPermissions = Object.values(PERMISSIONS);
                    const invalidPermissions = permissions.filter(
                        perm => !validPermissions.includes(perm)
                    );
                    
                    if (invalidPermissions.length > 0) {
                        return res.status(400).json({
                            success: false,
                            error: \`Invalid permissions: \${invalidPermissions.join(', ')}\`
                        });
                    }
                    
                    // Update permissions
                    user.permissions = permissions;
                    await user.save();
                    
                    res.json({
                        success: true,
                        message: 'User permissions updated'
                    });
                } catch (error) {
                    console.error('Permission update error:', error);
                    res.status(500).json({
                        success: false,
                        error: 'Failed to update permissions'
                    });
                }
            }
        );
    </pre>
</div>

