<pre>
&lt;?php
// config/database.php
// NEVER commit this file to version control!

class DatabaseConfig {
    // Development Environment
    const DEV_HOST = 'localhost';
    const DEV_NAME = 'finance_app';
    const DEV_USER = 'finance_user';
    const DEV_PASS = 'StrongPassword123!';
    const DEV_PORT = 3306;
    const DEV_CHARSET = 'utf8mb4';
    
    // Production Environment (example - use environment variables)
    const PROD_HOST = '';
    const PROD_NAME = '';
    const PROD_USER = '';
    const PROD_PASS = '';
    const PROD_PORT = 3306;
    const PROD_CHARSET = 'utf8mb4';
    
    // Connection options
    public static function getOptions() {
        return [
            PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,
            PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
            PDO::ATTR_EMULATE_PREPARES   => false,
            PDO::ATTR_PERSISTENT         => false, // Don't use persistent connections
            PDO::ATTR_STRINGIFY_FETCHES  => false,
            PDO::MYSQL_ATTR_INIT_COMMAND => "SET time_zone = '+00:00'",
            PDO::MYSQL_ATTR_COMPRESS     => true, // Enable compression
            PDO::MYSQL_ATTR_SSL_CA       => null, // Set for SSL connections
        ];
    }
    
    // Get DSN (Data Source Name)
    public static function getDsn($environment = 'dev') {
        $host = $environment === 'prod' ? self::PROD_HOST : self::DEV_HOST;
        $port = $environment === 'prod' ? self::PROD_PORT : self::DEV_PORT;
        $name = $environment === 'prod' ? self::PROD_NAME : self::DEV_NAME;
        $charset = $environment === 'prod' ? self::PROD_CHARSET : self::DEV_CHARSET;
        
        return "mysql:host={$host};port={$port};dbname={$name};charset={$charset}";
    }
    
    // Get credentials
    public static function getCredentials($environment = 'dev') {
        return [
            'username' => $environment === 'prod' ? self::PROD_USER : self::DEV_USER,
            'password' => $environment === 'prod' ? self::PROD_PASS : self::DEV_PASS
        ];
    }
}

// Alternative: Using environment variables (Recommended for production)
class EnvDatabaseConfig {
    public static function getConnection() {
        // Read from environment variables (set in .env file or server config)
        $host = getenv('DB_HOST') ?: 'localhost';
        $port = getenv('DB_PORT') ?: 3306;
        $name = getenv('DB_NAME') ?: 'finance_app';
        $user = getenv('DB_USER') ?: 'root';
        $pass = getenv('DB_PASS') ?: '';
        $charset = getenv('DB_CHARSET') ?: 'utf8mb4';
        
        $dsn = "mysql:host={$host};port={$port};dbname={$name};charset={$charset}";
        
        $options = [
            PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,
            PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
            PDO::ATTR_EMULATE_PREPARES   => false,
        ];
        
        // SSL configuration for production
        if (getenv('DB_SSL') === 'true') {
            $options[PDO::MYSQL_ATTR_SSL_CA] = getenv('DB_SSL_CA');
            $options[PDO::MYSQL_ATTR_SSL_VERIFY_SERVER_CERT] = false;
        }
        
        return [
            'dsn' => $dsn,
            'username' => $user,
            'password' => $pass,
            'options' => $options
        ];
    }
}
