<h3>PDO Connection Class</h3>

<b>Complete Database Connection Handler (includes/DB.php):</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
&lt;?php
// includes/DB.php - Main Database Connection Class

class DB {
    private static $instance = null;
    private $pdo;
    private $stmt;
    private $error;
    private $lastInsertId;
    
    // Private constructor (Singleton Pattern)
    private function __construct() {
        try {
            // Load configuration based on environment
            $config = $this->loadConfiguration();
            
            // Create PDO instance
            $this->pdo = new PDO(
                $config['dsn'],
                $config['username'],
                $config['password'],
                $config['options']
            );
            
            // Set additional attributes
            $this->pdo->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);
            $this->pdo->setAttribute(PDO::ATTR_STRINGIFY_FETCHES, false);
            
            // Set timezone to UTC for consistency
            $this->pdo->exec("SET time_zone = '+00:00'");
            
        } catch (PDOException $e) {
            $this->error = $e->getMessage();
            $this->logError($e);
            throw new Exception("Database connection failed: " . $e->getMessage());
        }
    }
    
    // Get singleton instance
    public static function getInstance() {
        if (self::$instance === null) {
            self::$instance = new self();
        }
        return self::$instance;
    }
    
    // Load configuration based on environment
    private function loadConfiguration() {
        $environment = getenv('APP_ENV') ?: 'development';
        
        if ($environment === 'production') {
            return [
                'dsn' => "mysql:host=" . getenv('DB_HOST') . 
                         ";port=" . (getenv('DB_PORT') ?: 3306) . 
                         ";dbname=" . getenv('DB_NAME') . 
                         ";charset=utf8mb4",
                'username' => getenv('DB_USER'),
                'password' => getenv('DB_PASS'),
                'options' => [
                    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
                    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
                    PDO::ATTR_PERSISTENT => false,
                    PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES utf8mb4 COLLATE utf8mb4_unicode_ci",
                    PDO::MYSQL_ATTR_FOUND_ROWS => true,
                ]
            ];
        } else {
            // Development configuration
            return [
                'dsn' => "mysql:host=localhost;port=3306;dbname=finance_app;charset=utf8mb4",
                'username' => 'finance_user',
                'password' => 'StrongPassword123!',
                'options' => [
                    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
                    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
                    PDO::ATTR_PERSISTENT => false,
                    PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES utf8mb4 COLLATE utf8mb4_unicode_ci",
                    PDO::MYSQL_ATTR_FOUND_ROWS => true,
                ]
            ];
        }
    }
    
    // Prepare statement with query
    public function query($sql) {
        $this->stmt = $this->pdo->prepare($sql);
        return $this;
    }
    
    // Bind values to prepared statement
    public function bind($param, $value, $type = null) {
        if (is_null($type)) {
            switch (true) {
                case is_int($value):
                    $type = PDO::PARAM_INT;
                    break;
                case is_bool($value):
                    $type = PDO::PARAM_BOOL;
                    break;
                case is_null($value):
                    $type = PDO::PARAM_NULL;
                    break;
                default:
                    $type = PDO::PARAM_STR;
            }
        }
        
        $this->stmt->bindValue($param, $value, $type);
        return $this;
    }
    
    // Execute the prepared statement
    public function execute() {
        try {
            $result = $this->stmt->execute();
            $this->lastInsertId = $this->pdo->lastInsertId();
            return $result;
        } catch (PDOException $e) {
            $this->error = $e->getMessage();
            $this->logError($e);
            throw $e;
        }
    }
    
    // Get result set as array of objects
    public function resultSet() {
        $this->execute();
        return $this->stmt->fetchAll(PDO::FETCH_OBJ);
    }
    
    // Get single record as object
    public function single() {
        $this->execute();
        return $this->stmt->fetch(PDO::FETCH_OBJ);
    }
    
    // Get single record as associative array
    public function singleArray() {
        $this->execute();
        return $this->stmt->fetch(PDO::FETCH_ASSOC);
    }
    
    // Get row count
    public function rowCount() {
        return $this->stmt->rowCount();
    }
    
    // Get last insert ID
    public function lastInsertId() {
        return $this->lastInsertId;
    }
    
    // Begin transaction
    public function beginTransaction() {
        return $this->pdo->beginTransaction();
    }
    
    // Commit transaction
    public function commit() {
        return $this->pdo->commit();
    }
    
    // Rollback transaction
    public function rollBack() {
        return $this->pdo->rollBack();
    }
    
    // Execute raw SQL (use with caution!)
    public function exec($sql) {
        try {
            return $this->pdo->exec($sql);
        } catch (PDOException $e) {
            $this->error = $e->getMessage();
            $this->logError($e);
            throw $e;
        }
    }
    
    // Get column metadata
    public function getColumnMeta($column_index = 0) {
        $this->execute();
        return $this->stmt->getColumnMeta($column_index);
    }
    
    // Debug prepared statement
    public function debugDumpParams() {
        return $this->stmt->debugDumpParams();
    }
    
    // Get PDO instance (advanced use only)
    public function getPdo() {
        return $this->pdo;
    }
    
    // Get error information
    public function getError() {
        return [
            'message' => $this->error,
            'info' => $this->stmt ? $this->stmt->errorInfo() : null
        ];
    }
    
    // Log errors to file
    private function logError($exception) {
        $logMessage = sprintf(
            "[%s] Database Error: %s in %s on line %d\nStack Trace:\n%s\n\n",
            date('Y-m-d H:i:s'),
            $exception->getMessage(),
            $exception->getFile(),
            $exception->getLine(),
            $exception->getTraceAsString()
        );
        
        // Log to file (in production, use proper logging service)
        if (getenv('APP_ENV') === 'production') {
            error_log($logMessage, 3, __DIR__ . '/../logs/database_errors.log');
        } else {
            // In development, also log to error_log
            error_log($logMessage);
        }
    }
    
    // Close connection (not usually needed with PDO)
    public function close() {
        $this->stmt = null;
        $this->pdo = null;
        self::$instance = null;
    }
    
    // Prevent cloning
    private function __clone() {}
    
    // Prevent unserialization
    public function __wakeup() {
        throw new Exception("Cannot unserialize singleton");
    }
}

// Helper function for quick database access
function db() {
    return DB::getInstance();
}
