&lt;?php
// includes/DB_mysqli.php - MySQLi Connection Class

class MySQLiDB {
    private static $instance = null;
    private $connection;
    private $stmt;
    
    // Private constructor
    private function __construct() {
        // Enable error reporting for MySQLi
        mysqli_report(MYSQLI_REPORT_ERROR | MYSQLI_REPORT_STRICT);
        
        try {
            $this->connection = new mysqli(
                'localhost',      // host
                'finance_user',   // username
                'StrongPassword123!', // password
                'finance_app',    // database
                3306              // port
            );
            
            // Set charset
            $this->connection->set_charset('utf8mb4');
            
            // Set timezone
            $this->connection->query("SET time_zone = '+00:00'");
            
        } catch (mysqli_sql_exception $e) {
            error_log("MySQLi Connection Error: " . $e->getMessage());
            throw new Exception("Database connection failed");
        }
    }
    
    // Get singleton instance
    public static function getInstance() {
        if (self::$instance === null) {
            self::$instance = new self();
        }
        return self::$instance;
    }
    
    // Prepare statement
    public function prepare($sql) {
        $this->stmt = $this->connection->prepare($sql);
        return $this;
    }
    
    // Bind parameters (for prepared statements)
    public function bindParams($types, ...$params) {
        $this->stmt->bind_param($types, ...$params);
        return $this;
    }
    
    // Execute statement
    public function execute() {
        $this->stmt->execute();
        return $this;
    }
    
    // Get result
    public function getResult() {
        return $this->stmt->get_result();
    }
    
    // Fetch all rows as associative array
    public function fetchAll() {
        $result = $this->getResult();
        return $result->fetch_all(MYSQLI_ASSOC);
    }
    
    // Fetch single row
    public function fetch() {
        $result = $this->getResult();
        return $result->fetch_assoc();
    }
    
    // Get affected rows
    public function affectedRows() {
        return $this->stmt->affected_rows;
    }
    
    // Get last insert ID
    public function insertId() {
        return $this->connection->insert_id;
    }
    
    // Begin transaction
    public function beginTransaction() {
        return $this->connection->begin_transaction();
    }
    
    // Commit transaction
    public function commit() {
        return $this->connection->commit();
    }
    
    // Rollback transaction
    public function rollback() {
        return $this->connection->rollback();
    }
    
    // Escape string (use prepared statements instead!)
    public function escape($string) {
        return $this->connection->real_escape_string($string);
    }
    
    // Close connection
    public function close() {
        if ($this->stmt) {
            $this->stmt->close();
        }
        $this->connection->close();
        self::$instance = null;
    }
    
    // Get MySQLi connection (advanced use)
    public function getConnection() {
        return $this->connection;
    }
}

