&lt;?php
// Example 1: Basic PDO Connection (Simple)
try {
    $pdo = new PDO(
        'mysql:host=localhost;dbname=finance_app;charset=utf8mb4',
        'finance_user',
        'StrongPassword123!',
        [
            PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
            PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
        ]
    );
    
    echo "Connected successfully!";
    
} catch (PDOException $e) {
    die("Connection failed: " . $e->getMessage());
}

// Example 2: Connection with Error Handling
function connectWithRetry($maxRetries = 3, $delay = 2) {
    $retryCount = 0;
    
    while ($retryCount < $maxRetries) {
        try {
            $pdo = new PDO(
                'mysql:host=localhost;dbname=finance_app',
                'finance_user',
                'StrongPassword123!',
                [
                    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
                    PDO::ATTR_PERSISTENT => true, // Persistent connection
                ]
            );
            
            return $pdo;
            
        } catch (PDOException $e) {
            $retryCount++;
            error_log("Connection attempt {$retryCount} failed: " . $e->getMessage());
            
            if ($retryCount < $maxRetries) {
                sleep($delay); // Wait before retry
            } else {
                throw new Exception("Failed to connect after {$maxRetries} attempts");
            }
        }
    }
}

// Example 3: Connection Pooling (using PDO attributes)
class ConnectionPool {
    private static $pool = [];
    private $maxConnections = 10;
    
    public function getConnection($name = 'default') {
        if (!isset(self::$pool[$name])) {
            if (count(self::$pool) >= $this->maxConnections) {
                throw new Exception("Maximum connections reached");
            }
            
            self::$pool[$name] = new PDO(
                'mysql:host=localhost;dbname=finance_app',
                'finance_user',
                'StrongPassword123!',
                [
                    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
                    PDO::ATTR_PERSISTENT => false,
                ]
            );
        }
        
        return self::$pool[$name];
    }
    
    public function closeConnection($name) {
        if (isset(self::$pool[$name])) {
            self::$pool[$name] = null;
            unset(self::$pool[$name]);
        }
    }
}

// Example 4: SSL-Encrypted Connection (Production)
function createSecureConnection() {
    $sslOptions = [
        PDO::MYSQL_ATTR_SSL_CA => '/path/to/ca-cert.pem',
        PDO::MYSQL_ATTR_SSL_CERT => '/path/to/client-cert.pem',
        PDO::MYSQL_ATTR_SSL_KEY => '/path/to/client-key.pem',
        PDO::MYSQL_ATTR_SSL_VERIFY_SERVER_CERT => false, // Set to true in production
    ];
    
    $options = [
        PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
        PDO::ATTR_PERSISTENT => false,
    ];
    
    $options = array_merge($options, $sslOptions);
    
    return new PDO(
        'mysql:host=production-db.example.com;dbname=finance_app',
        'prod_user',
        getenv('DB_PASSWORD'), // From environment variable
        $options
    );
}

// Example 5: Read/Write Separation (Master-Slave)
class ReadWriteConnection {
    private $writeConnection;
    private $readConnection;
    
    public function __construct() {
        // Write connection (Master)
        $this->writeConnection = new PDO(
            'mysql:host=master-db.example.com;dbname=finance_app',
            'write_user',
            'WritePassword123!',
            [PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION]
        );
        
        // Read connection (Slave)
        $this->readConnection = new PDO(
            'mysql:host=slave-db.example.com;dbname=finance_app',
            'read_user',
            'ReadPassword123!',
            [PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION]
        );
    }
    
    public function getWriteConnection() {
        return $this->writeConnection;
    }
    
    public function getReadConnection() {
        return $this->readConnection;
    }
    
    // Automatically choose connection based on query type
    public function getConnection($queryType = 'read') {
        return $queryType === 'write' 
            ? $this->writeConnection 
            : $this->readConnection;
    }
}

// Example 6: Connection with Timeout Settings
function createConnectionWithTimeout() {
    $options = [
        PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
        PDO::ATTR_TIMEOUT => 5, // Connection timeout in seconds
        PDO::MYSQL_ATTR_INIT_COMMAND => "SET SESSION wait_timeout=300", // 5 minutes
        PDO::MYSQL_ATTR_READ_DEFAULT_FILE => "/etc/my.cnf", // Read from config file
    ];
    
    $pdo = new PDO(
        'mysql:host=localhost;dbname=finance_app',
        'finance_user',
        'StrongPassword123!',
        $options
    );
    
    // Additional timeout settings
    $pdo->exec("SET net_read_timeout=30");
    $pdo->exec("SET net_write_timeout=60");
    
    return $pdo;
}
