-- SQL to create secure database users for our application

-- 1. Create Application User (Limited Privileges)
CREATE USER 'finance_app'@'localhost' IDENTIFIED BY 'StrongAppPassword123!';

-- Grant minimum required privileges
GRANT SELECT, INSERT, UPDATE, DELETE ON finance_app.* TO 'finance_app'@'localhost';
GRANT EXECUTE ON finance_app.* TO 'finance_app'@'localhost'; -- For stored procedures

-- 2. Create Backup User (Read-only + lock tables)
CREATE USER 'finance_backup'@'localhost' IDENTIFIED BY 'StrongBackupPassword123!';
GRANT SELECT, LOCK TABLES, RELOAD ON *.* TO 'finance_backup'@'localhost';

-- 3. Create Reporting User (Read-only)
CREATE USER 'finance_report'@'localhost' IDENTIFIED BY 'StrongReportPassword123!';
GRANT SELECT ON finance_app.* TO 'finance_report'@'localhost';

-- 4. Create Migration User (Full access for deployments)
CREATE USER 'finance_migrate'@'localhost' IDENTIFIED BY 'StrongMigratePassword123!';
GRANT ALL PRIVILEGES ON finance_app.* TO 'finance_migrate'@'localhost';
GRANT CREATE USER, RELOAD ON *.* TO 'finance_migrate'@'localhost';

-- 5. Apply changes
FLUSH PRIVILEGES;

-- 6. Verify user privileges
SHOW GRANTS FOR 'finance_app'@'localhost';
SHOW GRANTS FOR 'finance_backup'@'localhost';
SHOW GRANTS FOR 'finance_report'@'localhost';
SHOW GRANTS FOR 'finance_migrate'@'localhost';

-- 7. Create database if not exists
CREATE DATABASE IF NOT EXISTS finance_app 
CHARACTER SET utf8mb4 
COLLATE utf8mb4_unicode_ci;

