<h3>Error Handling and Debugging</h3>

<b>Comprehensive Error Handling Strategies:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
&lt;?php
// Error Handling Class for Database Operations
class DatabaseErrorHandler {
    
    // Custom error handler
    public static function handleError($error, $sql = null, $params = null) {
        $errorInfo = [
            'timestamp' => date('Y-m-d H:i:s'),
            'error' => $error,
            'sql' => $sql,
            'params' => $params,
            'backtrace' => debug_backtrace(DEBUG_BACKTRACE_IGNORE_ARGS, 5),
            'server' => $_SERVER['SERVER_NAME'] ?? 'CLI',
            'ip' => $_SERVER['REMOTE_ADDR'] ?? '127.0.0.1',
            'user_agent' => $_SERVER['HTTP_USER_AGENT'] ?? 'Unknown',
        ];
        
        // Log to file
        self::logToFile($errorInfo);
        
        // Send email alert for critical errors
        if (self::isCriticalError($error)) {
            self::sendAlert($errorInfo);
        }
        
        // Return user-friendly message
        return self::getUserMessage($error);
    }
    
    // Log error to file
    private static function logToFile($errorInfo) {
        $logDir = __DIR__ . '/../logs';
        
        // Create logs directory if it doesn't exist
        if (!is_dir($logDir)) {
            mkdir($logDir, 0755, true);
        }
        
        $logFile = $logDir . '/database_errors_' . date('Y-m-d') . '.log';
        
        $logEntry = sprintf(
            "[%s] %s\nSQL: %s\nParams: %s\nBacktrace:\n%s\n\n",
            $errorInfo['timestamp'],
            $errorInfo['error'],
            $errorInfo['sql'] ?? 'N/A',
            json_encode($errorInfo['params']),
            print_r($errorInfo['backtrace'], true)
        );
        
        file_put_contents($logFile, $logEntry, FILE_APPEND | LOCK_EX);
    }
    
    // Send alert for critical errors
    private static function sendAlert($errorInfo) {
        $to = getenv('ADMIN_EMAIL') ?: 'admin@example.com';
        $subject = 'Database Critical Error Alert';
        
        $message = "Critical Database Error Occurred:\n\n";
        $message .= "Time: " . $errorInfo['timestamp'] . "\n";
        $message .= "Error: " . $errorInfo['error'] . "\n";
        $message .= "Server: " . $errorInfo['server'] . "\n";
        $message .= "IP: " . $errorInfo['ip'] . "\n";
        
        // In production, use proper mail function
        if (getenv('APP_ENV') === 'production') {
            mail($to, $subject, $message);
        }
    }
    
    // Check if error is critical
    private static function isCriticalError($error) {
        $criticalPatterns = [
            '/lost connection/i',
            '/server has gone away/i',
            '/too many connections/i',
            '/access denied/i',
            '/table .* doesn\'t exist/i',
        ];
        
        foreach ($criticalPatterns as $pattern) {
            if (preg_match($pattern, $error)) {
                return true;
            }
        }
        
        return false;
    }
    
    // Get user-friendly error message
    private static function getUserMessage($error) {
        // Map technical errors to user-friendly messages
        $errorMap = [
            '/SQLSTATE\[HY000\] \[2002\]/i' => 'Database server is unreachable. Please try again later.',
            '/access denied/i' => 'Database access error. Contact administrator.',
            '/duplicate entry/i' => 'This record already exists.',
            '/cannot be null/i' => 'Required fields are missing.',
            '/foreign key constraint/i' => 'Cannot delete this record as it is referenced elsewhere.',
            '/table .* doesn\'t exist/i' => 'Database configuration error.',
        ];
        
        foreach ($errorMap as $pattern => $message) {
            if (preg_match($pattern, $error)) {
                return $message;
            }
        }
        
        // Default message for unknown errors
        return 'A database error occurred. Our team has been notified.';
    }
}

// Usage example with PDO
try {
    $pdo = new PDO(
        'mysql:host=localhost;dbname=finance_app',
        'finance_user',
        'StrongPassword123!',
        [PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION]
    );
    
    // Example query that might fail
    $stmt = $pdo->prepare("SELECT * FROM non_existent_table WHERE id = :id");
    $stmt->execute(['id' => 1]);
    
} catch (PDOException $e) {
    $userMessage = DatabaseErrorHandler::handleError(
        $e->getMessage(),
        $stmt->queryString ?? null,
        ['id' => 1]
    );
    
    // Show user-friendly message
    echo "Error: " . $userMessage;
    
    // For developers, show more details in development
    if (getenv('APP_ENV') !== 'production') {
        echo "\n\nTechnical Details (Development only):\n";
        echo $e->getMessage();
    }
}

// Debugging helper functions
class DatabaseDebugger {
    
    // Get connection information
    public static function getConnectionInfo($pdo) {
        return [
            'driver' => $pdo->getAttribute(PDO::ATTR_DRIVER_NAME),
            'client_version' => $pdo->getAttribute(PDO::ATTR_CLIENT_VERSION),
            'server_version' => $pdo->getAttribute(PDO::ATTR_SERVER_VERSION),
            'connection_status' => $pdo->getAttribute(PDO::ATTR_CONNECTION_STATUS),
            'server_info' => $pdo->getAttribute(PDO::ATTR_SERVER_INFO),
        ];
    }
    
    // Monitor query performance
    public static function monitorQuery($callback, $label = 'Query') {
        $startTime = microtime(true);
        $startMemory = memory_get_usage();
        
        $result = $callback();
        
        $endTime = microtime(true);
        $endMemory = memory_get_usage();
        
        $executionTime = round(($endTime - $startTime) * 1000, 2); // milliseconds
        $memoryUsed = round(($endMemory - $startMemory) / 1024, 2); // KB
        
        error_log("[$label] Time: {$executionTime}ms, Memory: {$memoryUsed}KB");
        
        return $result;
    }
    
    // Explain query execution plan
    public static function explainQuery($pdo, $sql, $params = []) {
        $explainSql = "EXPLAIN " . $sql;
        $stmt = $pdo->prepare($explainSql);
        $stmt->execute($params);
        
        return $stmt->fetchAll(PDO::FETCH_ASSOC);
    }
}

// Example usage of debugger
$pdo = new PDO('mysql:host=localhost;dbname=finance_app', 'user', 'pass');

// Get connection info
$info = DatabaseDebugger::getConnectionInfo($pdo);
print_r($info);

// Monitor query performance
$users = DatabaseDebugger::monitorQuery(function() use ($pdo) {
    $stmt = $pdo->query("SELECT * FROM users");
    return $stmt->fetchAll();
}, "Fetch All Users");

// Explain query
$explain = DatabaseDebugger::explainQuery(
    $pdo, 
    "SELECT * FROM users WHERE email = ?", 
    ['test@example.com']
);
print_r($explain);
