            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;td&gt;&lt;?php echo date('F j, Y', strtotime($user['created_at'])); ?&gt;&lt;/td&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;td&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;span class="&lt;?php echo $user['active'] ? 'active' : 'inactive'; ?&gt;"&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;?php echo $user['active'] ? 'Active' : 'Inactive'; ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/span&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/td&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;td&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;a href="view.php?id=&lt;?php echo $user['id']; ?&gt;"&gt;View&lt;/a&gt; |<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;a href="edit.php?id=&lt;?php echo $user['id']; ?&gt;"&gt;Edit&lt;/a&gt; |<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;a href="delete.php?id=&lt;?php echo $user['id']; ?&gt;" onclick="return confirm('Are you sure?')"&gt;Delete&lt;/a&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/td&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/tr&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;?php endforeach; ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;?php endif; ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;/tbody&gt;<br/>
            &nbsp;&nbsp;&lt;/table&gt;<br/>
            &lt;/body&gt;<br/>
            &lt;/html&gt;
        </pre>
    </div>
    
    <b>Key Points in the Code:</b>
    <div style="background-color: #f0f8ff; padding: 15px; margin: 10px 0; border-radius: 5px;">
        1. <b>htmlspecialchars()</b>: Prevents XSS attacks by escaping HTML characters<br/>
        2. <b>colspan</b>: Merges cells for "no results" message<br/>
        3. <b>Conditional formatting</b>: Different CSS classes based on data<br/>
        4. <b>Date formatting</b>: Converting MySQL datetime to readable format<br/>
        5. <b>Link parameters</b>: Passing IDs in URLs for CRUD operations<br/>
        6. <b>Confirmation dialog</b>: JavaScript confirm() for delete actions
    </div>
    
    <hr/>
    
    <b>d. Advanced Display Techniques</b><br/>
    
    <b>Pagination Example:</b>
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            &lt;?php<br/>
            // Pagination variables<br/>
            $limit = 10; // Number of records per page<br/>
            $page = isset($_GET['page']) ? (int)$_GET['page'] : 1;<br/>
            $offset = ($page - 1) * $limit;<br/>
            <br/>
            // Get total number of records<br/>
            $sql = "SELECT COUNT(*) as total FROM users";<br/>
            $stmt = $pdo->query($sql);<br/>
            $totalRecords = $stmt->fetch()['total'];<br/>
            <br/>
            // Calculate total pages<br/>
            $totalPages = ceil($totalRecords / $limit);<br/>
            <br/>
            // Get records for current page<br/>
            $sql = "SELECT * FROM users LIMIT :limit OFFSET :offset";<br/>
            $stmt = $pdo->prepare($sql);<br/>
            $stmt->bindValue(':limit', $limit, PDO::PARAM_INT);<br/>
            $stmt->bindValue(':offset', $offset, PDO::PARAM_INT);<br/>
            $stmt->execute();<br/>
            $users = $stmt->fetchAll();<br/>
            ?&gt;<br/>
            <br/>
            &lt;!-- Display pagination controls --&gt;<br/>
            &lt;div class="pagination"&gt;<br/>
            &nbsp;&nbsp;&lt;?php if ($page > 1): ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;a href="?page=&lt;?php echo $page - 1; ?&gt;"&gt;&amp;laquo; Previous&lt;/a&gt;<br/>
            &nbsp;&nbsp;&lt;?php endif; ?&gt;<br/>
            <br/>
            &nbsp;&nbsp;&lt;?php for ($i = 1; $i <= $totalPages; $i++): ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;?php if ($i == $page): ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;span class="current"&gt;&lt;?php echo $i; ?&gt;&lt;/span&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;?php else: ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;a href="?page=&lt;?php echo $i; ?&gt;"&gt;&lt;?php echo $i; ?&gt;&lt;/a&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;?php endif; ?&gt;<br/>
            &nbsp;&nbsp;&lt;?php endfor; ?&gt;<br/>
            <br/>
            &nbsp;&nbsp;&lt;?php if ($page < $totalPages): ?&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;a href="?page=&lt;?php echo $page + 1; ?&gt;"&gt;Next &amp;raquo;&lt;/a&gt;<br/>
            &nbsp;&nbsp;&lt;?php endif; ?&gt;<br/>
            &lt;/div&gt;
        </pre>
    </div>
    
    <b>Search and Filter Example:</b>
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            &lt;?php<br/>
