            &nbsp;&nbsp;$sql .= " AND (name LIKE :search OR email LIKE :search)";<br/>
            &nbsp;&nbsp;$params[':search'] = "%$searchTerm%";<br/>
            }<br/>
            <br/>
            // Add status filter<br/>
