            &nbsp;&nbsp;$params[':status'] = ($statusFilter == 'active') ? 1 : 0;<br/>
            }<br/>
            <br/>
            $sql .= " ORDER BY created_at DESC";<br/>
            <br/>
            $stmt = $pdo->prepare($sql);<br/>
            $stmt->execute($params);<br/>
            $users = $stmt->fetchAll();<br/>
