            &nbsp;&nbsp;&lt;select name="status"&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;option value=""&gt;All Statuses&lt;/option&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;option value="active" &lt;?php echo $statusFilter == 'active' ? 'selected' : ''; ?&gt;&gt;Active&lt;/option&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;option value="inactive" &lt;?php echo $statusFilter == 'inactive' ? 'selected' : ''; ?&gt;&gt;Inactive&lt;/option&gt;<br/>
            &nbsp;&nbsp;&lt;/select&gt;<br/>
            &nbsp;&nbsp;&lt;button type="submit"&gt;Search&lt;/button&gt;<br/>
            &nbsp;&nbsp;&lt;a href="?"&gt;Clear Filters&lt;/a&gt;<br/>
            &lt;/form&gt;
        </pre>
    </div>
    
    <hr/>
    
    <h3>Hands-On Exercise: Product Management System</h3>
    
    <b>Task:</b> Create a product catalog that displays items from a database<br/><br/>
    
    <b>Database Structure:</b>
    <div style="background-color: #f0f8ff; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Table: products</b><br/>
        CREATE TABLE products (<br/>
        &nbsp;&nbsp;id INT PRIMARY KEY AUTO_INCREMENT,<br/>
        &nbsp;&nbsp;name VARCHAR(100) NOT NULL,<br/>
        &nbsp;&nbsp;description TEXT,<br/>
        &nbsp;&nbsp;price DECIMAL(10,2) NOT NULL,<br/>
        &nbsp;&nbsp;category_id INT,<br/>
        &nbsp;&nbsp;stock_quantity INT DEFAULT 0,<br/>
        &nbsp;&nbsp;image_url VARCHAR(255),<br/>
        &nbsp;&nbsp;created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,<br/>
        &nbsp;&nbsp;updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP<br/>
        );<br/>
        <br/>
        <b>Table: categories</b><br/>
        CREATE TABLE categories (<br/>
        &nbsp;&nbsp;id INT PRIMARY KEY AUTO_INCREMENT,<br/>
        &nbsp;&nbsp;name VARCHAR(50) NOT NULL,<br/>
        &nbsp;&nbsp;description TEXT<br/>
        );
