    </div>
    
    <b>Starter Code:</b>
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            &lt;?php<br/>
            require_once 'config/database.php';<br/>
            <br/>
            // Handle filters<br/>
            $categoryId = isset($_GET['category']) ? (int)$_GET['category'] : 0;<br/>
            $minPrice = isset($_GET['min_price']) ? (float)$_GET['min_price'] : 0;<br/>
            $maxPrice = isset($_GET['max_price']) ? (float)$_GET['max_price'] : 1000;<br/>
            $search = isset($_GET['search']) ? $_GET['search'] : '';<br/>
            $sort = isset($_GET['sort']) ? $_GET['sort'] : 'newest';<br/>
            <br/>
            // Build query<br/>
            $sql = "SELECT p.*, c.name as category_name <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FROM products p <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;LEFT JOIN categories c ON p.category_id = c.id <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;WHERE 1=1";<br/>
            <br/>
            $params = [];<br/>
            <br/>
            if ($categoryId > 0) {<br/>
            &nbsp;&nbsp;$sql .= " AND p.category_id = :category_id";<br/>
            &nbsp;&nbsp;$params[':category_id'] = $categoryId;<br/>
            }<br/>
            <br/>
            if (!empty($search)) {<br/>
            &nbsp;&nbsp;$sql .= " AND (p.name LIKE :search OR p.description LIKE :search)";<br/>
            &nbsp;&nbsp;$params[':search'] = "%$search%";<br/>
            }<br/>
            <br/>
            $sql .= " AND p.price BETWEEN :min_price AND :max_price";<br/>
            $params[':min_price'] = $minPrice;<br/>
            $params[':max_price'] = $maxPrice;<br/>
            <br/>
            // Add sorting<br/>
            switch ($sort) {<br/>
            &nbsp;&nbsp;case 'price_low':<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$sql .= " ORDER BY p.price ASC";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;case 'price_high':<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$sql .= " ORDER BY p.price DESC";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;case 'name':<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$sql .= " ORDER BY p.name ASC";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;default:<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$sql .= " ORDER BY p.created_at DESC";<br/>
            }<br/>
            <br/>
            // Execute query<br/>
            $stmt = $pdo->prepare($sql);<br/>
            $stmt->execute($params);<br/>
            $products = $stmt->fetchAll();<br/>
            ?&gt;
        </pre>
