            <br/>
            &nbsp;&nbsp;// Sanitize inputs<br/>
            &nbsp;&nbsp;$username = clean_input($_POST['username']);<br/>
            &nbsp;&nbsp;$email = filter_var($_POST['email'], FILTER_SANITIZE_EMAIL);<br/>
