            &nbsp;&nbsp;$last_name = clean_input($_POST['last_name']);<br/>
            <br/>
            &nbsp;&nbsp;// Validation<br/>
            &nbsp;&nbsp;if (empty($username)) $errors[] = "Username is required";<br/>
            &nbsp;&nbsp;if (!filter_var($email, FILTER_VALIDATE_EMAIL)) $errors[] = "Invalid email format";<br/>
            &nbsp;&nbsp;if (strlen($password) < 8) $errors[] = "Password must be at least 8 characters";<br/>
            &nbsp;&nbsp;if ($password !== $confirm_password) $errors[] = "Passwords do not match";<br/>
            &nbsp;&nbsp;if (!preg_match('/[A-Z]/', $password)) $errors[] = "Password must contain at least one uppercase letter";<br/>
            &nbsp;&nbsp;if (!preg_match('/[a-z]/', $password)) $errors[] = "Password must contain at least one lowercase letter";<br/>
            &nbsp;&nbsp;if (!preg_match('/[0-9]/', $password)) $errors[] = "Password must contain at least one number";<br/>
            <br/>
            &nbsp;&nbsp;// Check if username/email already exists<br/>
            &nbsp;&nbsp;if (empty($errors)) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("SELECT id FROM users WHERE username = ? OR email = ?");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$username, $email]);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if ($stmt->rowCount() > 0) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Username or email already exists";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;// Create user if no errors<br/>
            &nbsp;&nbsp;if (empty($errors)) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;// Hash password with current best practice<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$password_hash = password_hash($password, PASSWORD_DEFAULT);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;// Generate verification token<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$verification_token = bin2hex(random_bytes(32));<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$verification_expires = date('Y-m-d H:i:s', strtotime('+24 hours'));<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;// Insert user<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("INSERT INTO users (username, email, password_hash, first_name, last_name, verification_token, verification_expires) VALUES (?, ?, ?, ?, ?, ?, ?)");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$username, $email, $password_hash, $first_name, $last_name, $verification_token, $verification_expires]);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;// Send verification email<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$verification_link = "https://yourdomain.com/verify.php?token=$verification_token";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$subject = "Verify Your Email Address";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$message = "Click the link to verify your email: $verification_link";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$headers = "From: no-reply@yourdomain.com";<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if (mail($email, $subject, $message, $headers)) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$success = true;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;} else {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Failed to send verification email";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;}<br/>
            }<br/>
            <br/>
            // Generate CSRF token for form<br/>
            $_SESSION['csrf_token'] = bin2hex(random_bytes(32));<br/>
            ?&gt;
        </pre>
    </div>
    
