    <b>c. Secure Login System</b><br/>
    
    <b>Login System (login.php):</b>
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            &lt;?php<br/>
            session_start();<br/>
            require_once 'includes/config.php';<br/>
            require_once 'includes/functions.php';<br/>
            <br/>
            $errors = [];<br/>
            <br/>
            // Check if user is already logged in<br/>
            if (isset($_SESSION['user_id'])) {<br/>
            &nbsp;&nbsp;header('Location: dashboard.php');<br/>
            &nbsp;&nbsp;exit();<br/>
            }<br/>
            <br/>
            if ($_SERVER['REQUEST_METHOD'] === 'POST') {<br/>
            &nbsp;&nbsp;// CSRF protection<br/>
            &nbsp;&nbsp;if (!isset($_POST['csrf_token']) || $_POST['csrf_token'] !== $_SESSION['csrf_token']) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;die('Invalid CSRF token');<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;$username = clean_input($_POST['username']);<br/>
            &nbsp;&nbsp;$password = $_POST['password'];<br/>
            &nbsp;&nbsp;$remember = isset($_POST['remember']);<br/>
            <br/>
            // Brute force protection: Check attempts in last hour<br/>
            $ip_address = $_SERVER['REMOTE_ADDR'];<br/>
            $stmt = $pdo->prepare("SELECT COUNT(*) as attempts FROM login_attempts WHERE ip_address = ? AND attempted_at > DATE_SUB(NOW(), INTERVAL 1 HOUR) AND success = 0");<br/>
            $stmt->execute([$ip_address]);<br/>
            $attempts = $stmt->fetch()['attempts'];<br/>
            <br/>
            if ($attempts >= 5) {<br/>
            &nbsp;&nbsp;$errors[] = "Too many login attempts. Please try again later.";<br/>
            } else {<br/>
            &nbsp;&nbsp;// Get user by username or email<br/>
            &nbsp;&nbsp;$stmt = $pdo->prepare("SELECT * FROM users WHERE username = ? OR email = ?");<br/>
            &nbsp;&nbsp;$stmt->execute([$username, $username]);<br/>
            &nbsp;&nbsp;$user = $stmt->fetch();<br/>
            <br/>
            &nbsp;&nbsp;if ($user) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;// Check if account is locked<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if ($user['locked_until'] && strtotime($user['locked_until']) > time()) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Account is temporarily locked. Please try again later.";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;} else if (!$user['is_active']) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Account is deactivated.";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;} else if (!$user['is_verified']) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Please verify your email first.";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;} else if (password_verify($password, $user['password_hash'])) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Successful login<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Record successful attempt<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("INSERT INTO login_attempts (ip_address, username, success) VALUES (?, ?, 1)");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$ip_address, $username]);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Reset login attempts<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("UPDATE users SET login_attempts = 0, locked_until = NULL, last_login = NOW() WHERE id = ?");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$user['id']]);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Set session variables<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$_SESSION['user_id'] = $user['id'];<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$_SESSION['username'] = $user['username'];<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$_SESSION['role'] = $user['role'];<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$_SESSION['login_time'] = time();<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Regenerate session ID to prevent fixation<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;session_regenerate_id(true);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Create persistent session if "remember me" is checked<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if ($remember) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$selector = bin2hex(random_bytes(16));<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$validator = bin2hex(random_bytes(32));<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$hashed_validator = hash('sha256', $validator);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$expires = date('Y-m-d H:i:s', strtotime('+30 days'));<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Store in database<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("INSERT INTO user_sessions (session_id, user_id, ip_address, user_agent, expires_at) VALUES (?, ?, ?, ?, ?)");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$selector . ':' . $hashed_validator, $user['id'], $ip_address, $_SERVER['HTTP_USER_AGENT'], $expires]);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Set cookie<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;setcookie('remember_me', $selector . ':' . $validator, [<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;'expires' => strtotime('+30 days'),<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;'path' => '/',<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;'domain' => $_SERVER['HTTP_HOST'],<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;'secure' => true,<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;'httponly' => true,<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;'samesite' => 'Strict'<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;]);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;header('Location: dashboard.php');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;exit();<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;} else {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Failed login<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("INSERT INTO login_attempts (ip_address, username, success) VALUES (?, ?, 0)");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$ip_address, $username]);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Increment login attempts<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("UPDATE users SET login_attempts = login_attempts + 1 WHERE id = ?");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$user['id']]);<br/>
            <br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Lock account after 5 failed attempts<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if ($user['login_attempts'] + 1 >= 5) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$locked_until = date('Y-m-d H:i:s', strtotime('+15 minutes'));<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("UPDATE users SET locked_until = ? WHERE id = ?");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$locked_until, $user['id']]);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Account locked for 15 minutes due to too many failed attempts.";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;} else {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Invalid username or password.";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;} else {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$errors[] = "Invalid username or password.";<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;// Record failed attempt with null username<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$stmt = $pdo->prepare("INSERT INTO login_attempts (ip_address, username, success) VALUES (?, NULL, 0)");<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;$stmt->execute([$ip_address]);<br/>
            &nbsp;&nbsp;}<br/>
            }<br/>
            }<br/>
            <br/>
            // Generate CSRF token<br/>
            $_SESSION['csrf_token'] = bin2hex(random_bytes(32));<br/>
            ?&gt;
        </pre>
    </div>
    
