            <br/>
            # Security headers<br/>
            Header always set X-Frame-Options "SAMEORIGIN"<br/>
            Header always set X-Content-Type-Options "nosniff"<br/>
            Header always set X-XSS-Protection "1; mode=block"<br/>
            Header always set Referrer-Policy "strict-origin-when-cross-origin"<br/>
            Header always set Content-Security-Policy "default-src 'self'; script-src 'self' 'unsafe-inline'; style-src 'self' 'unsafe-inline';"<br/>
            <br/>
            # Prevent directory listing<br/>
            Options -Indexes<br/>
            <br/>
            # Protect sensitive files<br/>
            &lt;FilesMatch "^\\.(htaccess|htpasswd|ini|log|sh|sql)$"&gt;<br/>
            &nbsp;&nbsp;Order Allow,Deny<br/>
