<b>Step-by-Step Implementation:</b><br />
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            // Step 1: Database Security Class<br />
            class SecureDatabase {<br />
                private $pdo;<br />
                <br />
                public function __construct($host, $dbname, $username, $password) {<br />
                    $dsn = "mysql:host=$host;dbname=$dbname;charset=utf8mb4";<br />
                    $options = [<br />
                        PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,<br />
                        PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,<br />
                        PDO::ATTR_EMULATE_PREPARES => false, // Real prepared statements<br />
                        PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES utf8mb4"<br />
                    ];<br />
                    $this->pdo = new PDO($dsn, $username, $password, $options);<br />
                }<br />
                <br />
                public function query($sql, $params = []) {<br />
                    $stmt = $this->pdo->prepare($sql);<br />
                    $stmt->execute($params);<br />
                    return $stmt;<br />
                }<br />
                <br />
                public function escape($input) {<br />
                    return htmlspecialchars($input, ENT_QUOTES, 'UTF-8');<br />
                }<br />
            }<br />
            <br />
            // Step 2: CSRF Protection Class<br />
            class CSRFProtection {<br />
                public static function generateToken() {<br />
                    if (empty($_SESSION['csrf_token'])) {<br />
                        $_SESSION['csrf_token'] = bin2hex(random_bytes(32));<br />
                    }<br />
                    return $_SESSION['csrf_token'];<br />
                }<br />
                <br />
                public static function validateToken($token) {<br />
                    if (empty($_SESSION['csrf_token']) || empty($token)) {<br />
                        return false;<br />
                    }<br />
                    return hash_equals($_SESSION['csrf_token'], $token);<br />
                }<br />
                <br />
                public static function getTokenField() {<br />
                    return '&lt;input type="hidden" name="csrf_token" value="' . <br />
                           self::generateToken() . '"&gt;';<br />
                }<br />
            }<br />
            <br />
            // Step 3: Input Validation Class<br />
            class InputValidator {<br />
                public static function sanitize($input, $type = 'string') {<br />
                    $input = trim($input);<br />
                    $input = stripslashes($input);<br />
                    <br />
                    switch ($type) {<br />
                        case 'email':<br />
                            return filter_var($input, FILTER_SANITIZE_EMAIL);<br />
                        case 'int':<br />
                            return filter_var($input, FILTER_SANITIZE_NUMBER_INT);<br />
                        case 'float':<br />
                            return filter_var($input, FILTER_SANITIZE_NUMBER_FLOAT);<br />
                        case 'url':<br />
                            return filter_var($input, FILTER_SANITIZE_URL);<br />
                        default:<br />
                            return htmlspecialchars($input, ENT_QUOTES, 'UTF-8');<br />
                    }<br />
                }<br />
                <br />
                public static function validate($input, $rules) {<br />
                    foreach ($rules as $rule => $value) {<br />
                        switch ($rule) {<br />
                            case 'required':<br />
                                if (empty($input)) return false;<br />
                                break;<br />
                            case 'min_length':<br />
                                if (strlen($input) < $value) return false;<br />
                                break;<br />
                            case 'max_length':<br />
                                if (strlen($input) > $value) return false;<br />
                                break;<br />
                            case 'email':<br />
                                if (!filter_var($input, FILTER_VALIDATE_EMAIL)) return false;<br />
                                break;<br />
                            case 'regex':<br />
                                if (!preg_match($value, $input)) return false;<br />
                                break;<br />
                        }<br />
                    }<br />
                    return true;<br />
                }<br />
            }<br />
        </pre>
    </div>

<b>Secure Login System Implementation:</b><br />
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            // secure-login.php<br />
            &lt;?php<br />
            require_once 'security-headers.php';<br />
            require_once 'SecureDatabase.php';<br />
            require_once 'CSRFProtection.php';<br />
            require_once 'InputValidator.php';<br />
            <br />
            session_start([<br />
                'cookie_lifetime' => 86400,<br />
                'cookie_secure' => true,<br />
                'cookie_httponly' => true,<br />
                'cookie_samesite' => 'Strict',<br />
                'use_strict_mode' => true,<br />
                'use_only_cookies' => true<br />
            ]);<br />
            <br />
            $db = new SecureDatabase('localhost', 'secure_app', 'app_user', 'StrongP@ssw0rd!');<br />
            <br />
            if ($_SERVER['REQUEST_METHOD'] === 'POST') {<br />
                // Validate CSRF token<br />
                if (!CSRFProtection::validateToken($_POST['csrf_token'] ?? '')) {<br />
                    die('Security error: Invalid CSRF token');<br />
                }<br />
                <br />
                // Sanitize and validate input<br />
                $username = InputValidator::sanitize($_POST['username']);<br />
                $password = $_POST['password']; // Don't sanitize password<br />
                <br />
                if (!InputValidator::validate($username, ['required' => true, 'min_length' => 3])) {<br />
                    die('Invalid username');<br />
                }<br />
                <br />
                // Secure password verification<br />
                $stmt = $db->query(<br />
                    "SELECT id, username, password, login_attempts, last_attempt <br />
                     FROM users WHERE username = ?",<br />
                    [$username]<br />
                );<br />
                <br />
                $user = $stmt->fetch();<br />
                <br />
                if ($user) {<br />
                    // Rate limiting check<br />
                    if ($user['login_attempts'] >= 5 && <br />
                        strtotime($user['last_attempt']) > time() - 900) { // 15 minutes<br />
                        die('Too many failed attempts. Try again in 15 minutes.');<br />
                    }<br />
                    <br />
                    // Verify password<br />
                    if (password_verify($password, $user['password'])) {<br />
                        // Reset failed attempts<br />
                        $db->query(<br />
                            "UPDATE users SET login_attempts = 0 WHERE id = ?",<br />
                            [$user['id']]<br />
                        );<br />
                        <br />
                        // Regenerate session ID<br />
                        session_regenerate_id(true);<br />
                        <br />
                        // Store user info in session<br />
                        $_SESSION['user_id'] = $user['id'];<br />
                        $_SESSION['username'] = $user['username'];<br />
                        $_SESSION['login_time'] = time();<br />
                        $_SESSION['user_agent'] = $_SERVER['HTTP_USER_AGENT'];<br />
                        $_SESSION['ip_address'] = $_SERVER['REMOTE_ADDR'];<br />
                        <br />
                        // Redirect to secure area<br />
                        header('Location: /dashboard.php');<br />
                        exit;<br />
                    } else {<br />
                        // Increment failed attempts<br />
                        $db->query(<br />
                            "UPDATE users SET login_attempts = login_attempts + 1, <br />
                             last_attempt = NOW() WHERE id = ?",<br />
                            [$user['id']]<br />
                        );<br />
                        $error = 'Invalid credentials';<br />
                    }<br />
                } else {<br />
                    // User doesn't exist - still delay to prevent timing attacks<br />
                    sleep(1);<br />
                    $error = 'Invalid credentials';<br />
                }<br />
            }<br />
            ?&gt;<br />
            <br />
            &lt;!DOCTYPE html&gt;<br />
            &lt;html lang="en"&gt;<br />
            &lt;head&gt;<br />
                &lt;meta charset="UTF-8"&gt;<br />
                &lt;meta name="viewport" content="width=device-width, initial-scale=1.0"&gt;<br />
                &lt;title&gt;Secure Login&lt;/title&gt;<br />
            &lt;/head&gt;<br />
            &lt;body&gt;<br />
                &lt;form method="POST"&gt;<br />
                    &lt;?= CSRFProtection::getTokenField() ?&gt;<br />
                    &lt;input type="text" name="username" required&gt;<br />
